headerButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let userService = $injector.get(widgetContext.servicesMap.get('userService'));

openAddUserDialog();

function openAddUserDialog() {
    customDialog.customDialog(htmlTemplate, AddUserDialogController).subscribe();
}

function AddUserDialogController(instance) {
    let vm = instance;

    vm.addEntityFormGroup = vm.fb.group({
        email: ['', [vm.validators.required, vm.validators.email]],
        firstName: [''],
        lastName: ['']
    });

    vm.cancel = function () {
        vm.dialogRef.close(null);
    };

    vm.save = function () {
        vm.addEntityFormGroup.markAsPristine();

        const formValues = vm.addEntityFormGroup.value;

        // ID del cliente "Usuarios", debe ser hardcoded (ajusta este ID real desde tu plataforma)
        const customerId = {
            entityType: 'CUSTOMER',
            id: 'c9a39b70-7317-11f0-9e5f-db09c3cbe3c9'  // <- AJUSTA ESTE ID A TU CASO
        };

        const tenantId = {
            entityType: 'TENANT',
            id: widgetContext.currentUser.userId
        };
        //const tenantId = widgetContext.currentUser.user.tenantId;

        const user = {
            email: formValues.email,
            firstName: formValues.firstName,
            lastName: formValues.lastName,
            authority: 'CUSTOMER_USER',
            customerId,
            tenantId,
            additionalInfo: {
                description: "",
                defaultDashboardId: "9cf20700-6f1c-11f0-9e5f-db09c3cbe3c9",
                defaultDashboardFullscreen: true,
                homeDashboardId: null,
                homeDashboardHideToolbar: true,
                userCredentialsEnabled: true,
                userActivated: true
            }
        };

        userService.saveUser(user, true).subscribe({
            next: function (savedUser) {
                widgetContext.showSuccessToast('Usuario creado y correo de activación enviado.');
                widgetContext.updateAliases();
                vm.dialogRef.close(savedUser);
            },
            error: function (err) {
                widgetContext.showErrorToast('Error al crear usuario: ' + err.message);
            }
        });
    };
}

