actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));

openDeleteDeviceDialog();

function openDeleteDeviceDialog() {
    let title = "Are you sure you want to delete the device " + entityName +  "?";
    let content = "Be careful, after the confirmation, the device and all related data will become unrecoverable!";
    dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
        function (result) {
            if (result) {
                deleteDevice();
            }
        }
    );
}

function deleteDevice() {
    deviceService.deleteDevice(entityId.id).subscribe(
        function () {
            widgetContext.updateAliases();
        }
    );
}

widgetContext.userService.getActivationLink(entityId.id).subscribe({
  next: (link) => {
    // 1. Copy to clipboard
    navigator.clipboard.writeText(link).then(() => {
      // 2. Show success toast
      widgetContext.showSuccessToast(`Activation link copied: ${link}`);
    }).catch(err => {
      console.error('Clipboard copy failed', err);
    });
  },
  error: (err) => {
    console.error('Error fetching activation link:', err);
  }
});

widgetContext.userService.sendActivationEmail(additionalParams.entity.Email).subscribe({
  next: () => {
    widgetContext.showSuccessToast(`Activation email sent to ${additionalParams.entity.Email}`);
  },
  error: (err) => {
    console.error('Error sending activation email:', err);
  }
});

