dataKeys.postFuncBody:
//return (value/60).toFixed(2);


const diff = value;

    if (isNaN(diff)) return "";


   var minutos =diff/60;
   var horas   =diff/3600;
   var dias    =diff/86400;

   if(diff>=60){

       return Math.floor(minutos.toFixed(2)) + " min";
   }
   else if(diff>=3600){
       return Math.floor(horas.toFixed(2)) + " h";
   }

   else if(diff>=86400){
       return dias.toFixed(2) + " d";
   }
   else{
       return Math.floor(diff) + " s";
   }


/*
    // value is in ms
    var totalMinutes = Math.floor(diff / (60 * 1000));
    var hours = Math.floor(totalMinutes / 60);
    var minutes = totalMinutes % 60;


    if (hours > 200){
        return " "
    } else if (hours === 0) {
        if (minutes === 0) {
            return " - ";
        }
        return minutes + " min";
    }

    return hours + " h" + (minutes > 0 ? " " + minutes + " min" : "");

*/

labelColor.colorFunction:
var temperature = value;
if (typeof temperature !== undefined) {
  var percent = (temperature + 60)/120 * 100;
  return tinycolor.mix('blue', 'red', percent).toHexString();
}
return 'blue';

