settings.markdownTextFunction:
//var estado = "EN OPERACIÓN";
//var cliente = "MODABUS S.A. DE C.V.";
//var serie = "X57196I";
//var operarios = "Juan Pérez, María Gómez";


// === Obtener el valor del key 'estado' en la data 
var estado = '';
var cliente= '';
var serie = '';
var nombre_operario= '';

if (ctx && ctx.data && ctx.data.length > 0) {
    // Buscar el datasource que contenga la telemetría 'estado'
  var estadoData = ctx.data.find(d => d.dataKey && d.dataKey.name === 'estado');
  if (estadoData && estadoData.data && estadoData.data.length > 0) {
      // Obtener el último valor
    estado = estadoData.data[estadoData.data.length - 1][1]; // último valor disponible
  }



    // Buscar el datasource que contenga la telemetría 'estado'
  var clienteData = ctx.data.find(d => d.dataKey && d.dataKey.name === 'cliente');
  if (clienteData && clienteData.data && clienteData.data.length > 0) {
      // Obtener el último valor
    cliente = clienteData.data[clienteData.data.length - 1][1]; // último valor disponible
  }



    // Buscar el datasource que contenga la telemetría 'estado'
  var serieData = ctx.data.find(d => d.dataKey && d.dataKey.name === 'serie');
  if (serieData && serieData.data && serieData.data.length > 0) {
      // Obtener el último valor
    serie = serieData.data[serieData.data.length - 1][1]; // último valor disponible
  }
}

if (ctx && ctx.data && ctx.data.length > 0) {
    // Buscar el datasource que contenga la telemetría 'estado'
  var nombre_operarioData = ctx.data.find(d => d.dataKey && d.dataKey.name === 'nombre_operario');
  if (nombre_operarioData && nombre_operarioData.data && nombre_operarioData.data.length > 0) {
      // Obtener el último valor
    nombre_operario = nombre_operarioData.data[nombre_operarioData.data.length - 1][1]; // último valor disponible
  }

    try {
      var parsed = JSON.parse(nombre_operario);
      if (Array.isArray(parsed)) {
        nombre_operario = parsed.join(", ");
      }
    } catch (e) {
      // Si no es JSON, verificar si ya es array real
      if (Array.isArray(nombre_operario)) {
        nombre_operario = nombre_operario.join(", ");
      }
    }

}









return `
  <div class="station-card">
    <div class="station-info">
      <h3>Estado actual de la estación</h3>
      <p><strong>ESTADO:</strong> ${estado}</p>
      <p><strong>CLIENTE:</strong> ${cliente}</p>
      <p><strong>SERIE:</strong> ${serie}</p>
      <p><strong>OPERARIO(S):</strong> ${nombre_operario}</p>
    </div>

  </div>
`;

