/* === Dark dialog override for .simple-text-popup ===
   Put this at the end of your custom CSS */
.tb-dark .simple-text-popup,
.tb-dark .simple-text-popup .mat-mdc-dialog-container,
.tb-dark .simple-text-popup .mat-mdc-dialog-surface,
.tb-dark .simple-text-popup .mat-mdc-dialog-content {
  background: #0D2743 !important;
  color: rgba(255,255,255,0.87) !important;
}

/* Fix the paragraph color that was forcing dark text */
.tb-dark .simple-text-popup p {
  color: rgba(255,255,255,0.87) !important;
  margin: 0 !important;
}

/* Toolbar/title */
.tb-dark .simple-text-popup mat-toolbar,
.tb-dark .simple-text-popup mat-toolbar .mat-mdc-toolbar-row {
  background: transparent !important;
  color: rgba(255,255,255,0.95) !important;
}
.tb-dark .simple-text-popup mat-toolbar h2 {
  color: inherit !important;
  margin: 0 !important;
  font-weight: 600 !important;
}

/* Dialog actions / buttons */
.tb-dark .simple-text-popup .mat-mdc-dialog-actions,
.tb-dark .simple-text-popup .mat-mdc-dialog-actions button,
.tb-dark .simple-text-popup button.mat-mdc-button {
  color: rgba(255,255,255,0.87) !important;
}
.tb-dark .simple-text-popup .mat-mdc-dialog-actions button:hover {
  background: rgba(255,255,255,0.06) !important;
}

/* Inputs / form fields inside the dialog */
.tb-dark .simple-text-popup .mat-mdc-form-field,
.tb-dark .simple-text-popup .mat-mdc-form-field input,
.tb-dark .simple-text-popup .mat-mdc-form-field textarea,
.tb-dark .simple-text-popup .mat-mdc-form-field select {
  background: transparent !important;
  color: rgba(255,255,255,0.87) !important;
  --mat-app-on-surface-variant: rgba(255,255,255,0.6) !important; /* variables used by MDC */
}

/* Select panels / options */
.tb-dark .simple-text-popup .mat-mdc-select-panel,
.tb-dark .simple-text-popup .mat-mdc-option {
  background: #0D2743 !important;
  color: rgba(255,255,255,0.87) !important;
}

/* Checkbox / radio visuals */
.tb-dark .simple-text-popup .mat-mdc-checkbox .mdc-checkbox__background,
.tb-dark .simple-text-popup .mat-mdc-radio .mdc-radio__outer-circle {
  border-color: rgba(255,255,255,0.6) !important;
}

/* Make the popup a consistent size and elevated */
.tb-dark .simple-text-popup {
  width: min(90vw, 720px) !important; /* safer than fixed vw */
  border-radius: 8px !important;
  box-shadow: 0 8px 24px rgba(0,0,0,0.6) !important;
}

/* In case the component sets inline color variables, override them */
.tb-dark .simple-text-popup[style] {
  background: #0D2743 !important;
  color: rgba(255,255,255,0.87) !important;
}

