/* --- Dark theme styling for simple-text-popup dialog --- */

/* Main popup container */
.simple-text-popup {
  background-color: #0D2743 !important;
  color: rgba(255, 255, 255, 0.87) !important;
  border-radius: 12px !important;
  box-shadow: 0 4px 25px rgba(0, 0, 0, 0.6) !important;
}

.mat-mdc-dialog-container .mat-mdc-dialog-content {
	color: #FFF !important;
}

/* Toolbar */
.simple-text-popup mat-toolbar[color="primary"] {
  background-color: #0A1F36 !important;
  color: rgba(255, 255, 255, 0.87) !important;
  border-top-left-radius: 12px !important;
  border-top-right-radius: 12px !important;
}

/* Content */
.simple-text-popup [mat-dialog-content] {
  background-color: transparent !important;
  color: rgba(255, 255, 255, 0.87) !important;
}

/* Divider */
.simple-text-popup mat-divider {
  border-top-color: rgba(255, 255, 255, 0.2) !important;
}

/* Actions area */
.simple-text-popup [mat-dialog-actions] {
  background-color: transparent !important;
}

/* Primary button */
.simple-text-popup button[mat-raised-button][color="primary"] {
  background-color: #F36900 !important;
  color: #fff !important;
  font-weight: 500;
}

/* Close icon */
.simple-text-popup button[mat-icon-button] mat-icon {
  color: rgba(255, 255, 255, 0.87) !important;
}

/* Hover effect for close icon */
.simple-text-popup button[mat-icon-button]:hover mat-icon {
  color: #ffffff !important;
}

/* Optional: add fade-in animation */
.simple-text-popup {
  animation: popup-fade 0.25s ease-in-out;
}
@keyframes popup-fade {
  from { opacity: 0; transform: scale(0.95); }
  to { opacity: 1; transform: scale(1); }
}
