/* Remove all elevation shadows from expansion panels */
::ng-deep .mat-expansion-panel {
  box-shadow: none !important;
}

/* Explicitly target the collapsed state */
::ng-deep .mat-expansion-panel:not(.mat-expanded) {
  box-shadow: none !important;
}

/* Also disable shadow on hover/focus */
::ng-deep .mat-expansion-panel:not(.mat-expanded):hover,
::ng-deep .mat-expansion-panel:not(.mat-expanded):focus {
  box-shadow: none !important;
}

/* Remove extra margin between panels */
mat-expansion-panel {
  margin: 0 !important;
}

/* Optional: tighter spacing between header and content */
mat-expansion-panel-header {
  padding: 8px 16px !important;  /* smaller header height */
  min-height: 40px !important;
}

mat-expansion-panel .mat-expansion-panel-content {
  margin: 0 !important;
  padding: 0 0 8px 0 !important; /* less bottom padding */
}

/* Optional: slim down the list items too */
mat-list-item {
  min-height: 32px !important;
  padding: 0 16px !important;
  font-size: 12px ;
}


mat-expansion-panel{
