elementClick.customFunction:
console.log("Causas de parada: ", widgetContext.stateController.getStateParams());

console.log(widgetContext.data);

// === Helper: Escapar campos CSV ===
function escapeCsvField(field) {
    if (field == null || field === undefined) return '""';
   var v = roundIfNumber(field);
    const s = String(v);
    return `"${s.replace(/"/g, '""')}"`;
}

function roundIfNumber(value) {
    if (value === null || value === undefined) return value;

    const num = Number(value);

    if (!isNaN(num)) {
        return num.toFixed(2);
    }

    return value;
}






// === Todas las series del widget ===
const series = widgetContext.data;



// === Desde el índice 2 en adelante están las series de datos ===
//const dataSeries = series.slice(2);



// === Desde el índice 2 en adelante, filtrando series con "Eficiencia Estación" ===
var dataSeries = series.slice(2).filter(s => {
    const label = s.dataKey?.label || s.dataKey?.name || "";
    return !label.includes("Eficiencia Estación");
});


// === Filtrar series para CSV: quitar las de FPY ===
var csvSeries = dataSeries.filter(s => { //cambio
    const label = s.dataKey?.label || ""; //cambio

    return label !== "Suma unidades_planificadasL" && label !== "Suma tiempo_activoL"  &&  label !== "Suma tiempo_estandarL" && label !== "Conteo operarios_registrados"   && label !== "ratio" && label !== "Suma unidades_ALAprimeraL"    ; //cambio
                                             
           
    
});




// === Solo mantener series con data ===
csvSeries = csvSeries.filter(s => Array.isArray(s.data) && s.data.length > 0); //cambio


// === GENERAR CABECERA DINÁMICA DESDE LOS LABELS ===
const dynamicHeaders = [
    "Linea",   // siempre fijo
    ...csvSeries.map(s => s.dataKey?.label || s.dataKey?.name || "Sin nombre")
];

// === Fusionar valores (solo 1 por serie) ===
const mergedValues = {};

csvSeries.forEach((serie, index) => {
    const label = serie.dataKey?.label || "";
    const isOperario = label.includes("Eficiencia "); // Detecta operarios por nombre

    if (isOperario) mergedValues[index] = new Set();

    serie.data.forEach(point => {
        let value = point[1];

        if (isOperario) {
            try {
                const arr = JSON.parse(value);
                if (Array.isArray(arr)) {
                    arr.forEach(n => mergedValues[index].add(n));
                    return;
                }
            } catch {}

            if (value) mergedValues[index].add(value);
            return;
        }

        // Series normales
        if (typeof value === "string" && value.startsWith('["')) {
            try {
                value = JSON.parse(value).join(", ");
            } catch {}
        }

        mergedValues[index] = value;
    });
});


// Convertir mergedValues a arreglo final
const finalValues = csvSeries.map((_, index) => {
    
   const label = csvSeries[index].dataKey?.label || "";
if (label.includes("Eficiencia ")) {
    return Array.from(mergedValues[index] || []).join(", ");
}

    return mergedValues[index] || "";
});


//*****************************************************************



// === Calcular cumplimiento del plan de produccion  === calcular OEE
let unidades_ensambladasL = 0;
let unidades_planificadasL= 0;

// === Calcular OEEE  ===
let tiempo_activoL = 0;
let tienpo_estandarL= 1;
let operarios_registrados= 1;
let ratio= 1;
let unidades_ALAprimeraL= 0;

dataSeries.forEach(serie => {
    const label = serie.dataKey?.label || "";

    if (label.includes("Producción total de la línea")) {
        unidades_ensambladasL = Number(serie.data?.[0]?.[1]) || 0;
    }

    if (label.includes("Suma unidades_planificadasL")) {
        unidades_planificadasL = Number(serie.data?.[0]?.[1]) || 0;
    }
    
    if (label.includes("Suma tiempo_activoL")) {
        tiempo_activoL = Number(serie.data?.[0]?.[1]) || 0;
    }

    if (label.includes("Suma tiempo_estandarL")) { 
        tienpo_estandarL = Number(serie.data?.[0]?.[1]) || 0;
    }
    
    if (label.includes("Conteo operarios_registrados")) {
        operarios_registrados = Number(serie.data?.[0]?.[1]) || 0;
    }
    
    if (label.includes("ratio")) {
        ratio_produccion = Number(serie.data?.[0]?.[1]) || 0;
    }
     if (label.includes("Suma unidades_ALAprimeraL")) {
        unidades_ALAprimeraL= Number(serie.data?.[0]?.[1]) || 0;
    }
});


let c_plan_p = 0;
if (unidades_planificadasL > 0) {
    c_plan_p = (unidades_ensambladasL*100) / unidades_planificadasL;
}

OEE = (tiempo_activoL*unidades_ALAprimeraL*10) / (tienpo_estandarL*operarios_registrados*ratio_produccion);

c_plan_p = roundIfNumber(c_plan_p);
OEE = roundIfNumber(OEE);



//******************************************************************






// === SINGLE ROW FINAL ===
const singleRow = [
    series[0]?.datasource?.entityName || "",
    ...finalValues
];

//*******************************************************************************
// === Procesar las paradas dinámicamente desde stateParams ===
const stateParams = widgetContext.stateController.getStateParams();

// Encontrar todos los índices de paradas dinámicamente
const paradaIndices = Object.keys(stateParams)
    .filter(k => k.startsWith("parada_") && k.endsWith("_key"))
    .map(k => k.match(/^parada_(\d+)_key$/)[1])
    .sort((a,b) => a-b); // ordenar por número

const extraHeaders = [];
const extraValues = [];

paradaIndices.forEach(n => {
    const key = stateParams[`parada_${n}_key`] || "";
    const tiempo = stateParams[`parada_${n}_tiempo`] || "";
    const cantidad = stateParams[`parada_${n}_cantidad`] || "";

    // Mayúsculas
    const keyUpper = String(key).toUpperCase();

    // Headers
    
    extraHeaders.push("CANTIDAD " + keyUpper);
    extraHeaders.push("TIEMPO " + keyUpper + " (min)");

    // Valores
    
    extraValues.push(cantidad);
    extraValues.push(tiempo);
});

// Agregar al CSV final

dynamicHeaders.push("Cumplimiento del plan de producción (%)");
dynamicHeaders.push("OEE (%)");
dynamicHeaders.push(...extraHeaders);

singleRow.push(c_plan_p);
singleRow.push(OEE);
singleRow.push(...extraValues);

//*******************************************************************************




//******************************************************************************
// === Obtener fecha en GMT-05 ===
function getDateTimeGMT5() {
    const options = {
        timeZone: 'America/Lima', // GMT-05 sin DST
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
        hour12: false
    };

    // Resultado típico: "11/24/2025, 14:33:51"
    const formatted = new Intl.DateTimeFormat('en-US', options).format(new Date());

    const [date, time] = formatted.split(', ');
    const [month, day, year] = date.split('/');

    // DEVUELVE: 2025-11-24_14-33-51
    return `${year}_${month}_${day}_${time.replace(/:/g, '-')}`;
}
const fechaGMT5 = getDateTimeGMT5();


// === Obtener entityName ===
var entityName = series[0]?.datasource?.entityName || "SinNombre";
// === Nombre final del archivo ===
const fileName = `Reporte-${entityName}-${fechaGMT5}.csv`;

//******************************************************************************
// === Construcción del CSV ===
const csvContent = [
    dynamicHeaders.map(escapeCsvField).join(","),   // <-- HEADERS DINÁMICOS
    singleRow.map(escapeCsvField).join(",")
].join("\n");

// === Descargar CSV ===
const blob = new Blob(["\uFEFF" + csvContent], { type: 'text/csv;charset=utf-8;' });
const link = document.createElement("a");
link.href = URL.createObjectURL(blob);




link.setAttribute("download", fileName);
document.body.appendChild(link);
link.click();
document.body.removeChild(link);
URL.revokeObjectURL(link.href);

console.log("CSV generado con headers dinámicos desde dataKey.label");

//-------------------------------------------------------------------------

