settings.cellContentFunction:
var westado = value;

var stateSvg = getStateSvg(westado);
var encodedStateSvg = encodeURIComponent(stateSvg);
var stateSvgUrl = 'data:image/svg+xml,' + encodedStateSvg;
var stateLabel = westado;

return '<span>'+
           '<img style="vertical-align: middle;" class="mat-icon" src="'+ stateSvgUrl +'">' +
           '<span style="vertical-align: middle; padding-left: 8px;">'+ stateLabel +'</span>' +
       '</span>';

function getStateSvg(westado) {
    switch (westado) {
        case 'EN OPERACIÓN':
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#4CE16B"/>' +
                   '</svg>';


        case 'EN PARADA':            
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#FF5144"/>' +
                   '</svg>';
        case 'EN INSPECCIÓN':
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' + 
                   '<circle cx="10" cy="10" r="6" fill="#FF2C73"/>' + '</svg>';
        case 'EN LEVANTAMIENTO':
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' + 
                   '<circle cx="10" cy="10" r="6" fill="#1EE9CC"/>' + '</svg>';
        case 'EN ESPERA':
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' + 
                   '<circle cx="10" cy="10" r="6" fill="#000"/>' + '</svg>';

        default:
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#AAAAAA"/>' +
                   '</svg>';
    }
}












