actionCellButton.customFunction:
widgetContext.dialogs.confirm(
  '¿Estás seguro?',
  `¿Desea aceptar la solicitud a la ${entityName}?`,
  'Cancelar',
  'Aceptar'
).subscribe(confirmed => {
  if (!confirmed) {
    return;
  }



const token = localStorage.getItem("jwt_token");

widgetContext.defaultSubscription.targetDevice = {
    deviceId: entityId.id,
    type: "device"
};

widgetContext.defaultSubscription.targetDeviceId = entityId.id;

widgetContext.defaultSubscription.targetEntityId = {
    entityType: "DEVICE",
    id: entityId.id
};
widgetContext.defaultSubscription.targetEntityName = entityName;

widgetContext.deviceService.sendOneWayRpcCommand(
    entityId.id,
    {
        method: "respuesta",            // method
        params: { value: "y" },          // params
    },

).subscribe(
    () => {
        console.log("RPC sent to:", entityName);
        
        widgetContext.showSuccessToast("Solicitud aprobada correctamente a " + entityName);
        console.log("RPC sent successfully:", entityName);
        
        // Find alarm related to this entity
        const alarms = widgetContext.defaultSubscription?.alarms?.data || [];
        const alarm = alarms.find(a => a.originator?.id === entityId.id && !a.cleared);
        
        
        if (alarm) {
            console.log("Found alarm to clear:", alarm);
        
            // Prepare the alarm clear API call
            const clearUrl = `https://dashboard.smelpro.com/api/alarm/${alarm.id.id}/clear`;
        
            fetch(clearUrl, {
              method: "POST",
              headers: {
                "Accept": "application/json",
                "X-Authorization": "Bearer " + token
              }
            })
            .then(clearRes => {
              if (!clearRes.ok) throw new Error("Alarm clear failed: " + clearRes.status);
              widgetContext.showSuccessToast("solicitud respondida correctamente");
              widgetContext.updateAliases();
              console.log("✅ Alarm cleared successfully:", alarm.id.id);
            })
            .catch(err => {
              widgetContext.showErrorToast("Error al resolver la solicitud");
              console.error("❌ Clear alarm error:", err);
            });
        
        } else {
            console.warn("No active alarm found for entity:", entityId);
            widgetContext.showWarningToast("No se encontró una solicitud activa para esta entidad");
        }
        
        
    },
    err => {
        widgetContext.showErrorToast("La respuesta a la solicitud no llegó a la estación");
        console.error("RPC Error:", err);
    }
);



  
});



