let match = entityName.match(/(\d+)\.(\d+)/);


let linea = parseInt(match[1], 10);
let estacion = parseInt(match[2], 10);
// Example output
//console.log("linea: ", linea, "estacion: ", estacion);

//var response = {
//    estacion: estacion,
//    linea: linea,
//    response: 'y',
//}


// Example parameters
const deviceId = "fa61f600-af6f-11f0-9f18-0d482a772853";
const url = `https://dashboard.smelpro.com/api/rpc/oneway/${deviceId}`;

// Method and params for the RPC
const body = {
  method: "response",
  params: {
    estacion: estacion,
    linea: linea,
    response: 'n',
  },
  persistent: false,
  timeout: 5000
};

// Get ThingsBoard’s current user token
const token = localStorage.getItem("jwt_token");

// Make the API call
fetch(url, {
  method: "POST",
  headers: {
    "Content-Type": "application/json",
    "X-Authorization": "Bearer " + token
  },
  body: JSON.stringify(body)
})
.then(res => {
  if (!res.ok) throw new Error("RPC request failed: " + res.status);
  return res.json().catch(() => ({})); // Handle empty responses gracefully
})
.then(data => {
  widgetContext.showSuccessToast("Solicitud aprobada correctamente");
  console.log("✅ RPC sent successfully:", data);
})
.catch(err => {
  widgetContext.showErrorToast("La respuesta a la solicitud no llegó a la estación");
  console.error("❌ RPC error:", err);
});

