configuration.tbelScript:
function nextRelation(metadata, msg) {
    if(msg.evento == "ALERTA") {

        if(msg.motivo=="ALERTA DE PARADA"){
            return ['ALERTA FIJA'];
        }
        else{
            return ['ALERTA DINAMICA'];
        }
    }
    else if(msg.evento == "INICIO_EMERGENCIA"){
        return ['INICIO_EMERGENCIA'];
    }
    else if(msg.evento == "FIN_EMERGENCIA" || msg.evento == "FIN_PARADA" ){
        return ['LIMPIAR FIJA'];
    }
    else if(msg.evento=="FIN"){
        return ['LIMPIAR DINAMICO'];
    }
    else {
        return ['NO'];
    }
}

return nextRelation(metadata, msg);

