//$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$
function tiempoSegundos(timeArray) {
    // Si no existe, devolver 0
    if (timeArray === undefined || timeArray === null) {
        return 0;
    }

    // Si no es string, intentar convertirlo a string
    if (typeof timeArray !== "string") {
        timeArray = String(timeArray);
    }

    // Verificar que contenga ":"
    if (timeArray.indexOf(":") === -1) {
        return 0;
    }

    // Separar en partes
    var partes = timeArray.split(":").map(function(x) { return parseInt(x, 10) || 0; });

    // dd:hh:mm:ss
    if (partes.length === 4) {
        return (partes[0] * 24 * 3600) + (partes[1] * 3600) + (partes[2] * 60) + partes[3];
    }

    // hh:mm:ss
    if (partes.length === 3) {
        return (partes[0] * 3600) + (partes[1] * 60) + partes[2];
    }

    // Cualquier otro formato
    return 0;
}

