descriptor.controllerScript:
self.onInit = function() {
    if (self.ctx.$scope.doughnutWidget && self.ctx.$scope.doughnutWidget.onInit) {
        self.ctx.$scope.doughnutWidget.onInit();
    }
};

self.onDataUpdated = function() {
    if (self.ctx.$scope.doughnutWidget && self.ctx.$scope.doughnutWidget.onDataUpdated) {
        self.ctx.$scope.doughnutWidget.onDataUpdated();
    }

    const data = self.ctx.data;
    if (!data || !data.length) return;

    // Usa la variable configurada o la primera
    let key = self.ctx.settings.centerKey || data[0].dataKey.name;
    const found = data.find(d => d.dataKey.name === key);

    if (found && found.data.length) {
        const valor = found.data.at(-1)[1];
        const elementoValor = document.getElementById('center-value');
        const elementoLabel = document.querySelector('#custom-center div:first-child'); 

        // Actualiza el valor
        if (elementoValor) {
            const esDecimal = valor % 1 !== 0;
            elementoValor.innerText = esDecimal ? valor.toFixed(2) : Math.round(valor);
        }

        // Actualiza la etiqueta arriba del n√∫mero
        if (elementoLabel) {
            elementoLabel.innerText = found.dataKey.label || found.dataKey.name;
        }
    }
};


self.typeParameters = function() {
    return {
        previewWidth: '380px',
        previewHeight: '300px',
        embedTitlePanel: true,
        embedActionsPanel: true,
        supportsUnitConversion: true,
        defaultDataKeysFunction: function() {
            return [
                { name: 'ejemplo1', label: 'Ejemplo 1', type: 'timeseries', color: '#4CAF50' },
                { name: 'ejemplo2', label: 'Ejemplo 2', type: 'timeseries', color: '#FFC107' }
            ];
        }
    };
};

self.onDestroy = function() {};

