descriptor.templateCss:
.mini-card {
    --icon-size: 40px;
    --value-size: 24px;

    width: 100%;
    height: 100%;
    border-radius: 10px;

    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

    text-align: center;
    font-family: inherit;
    color: #212121;
}

.mini-icon {
    display: flex;
    justify-content: center;
    align-items: center;
}

.mini-value {
    margin-top: 6px;
    font-size: var(--value-size);
    font-weight: 600;
}

.mat-icon {
    width: var(--icon-size);
    height: var(--icon-size);
    font-size: var(--icon-size);
}

descriptor.controllerScript:
self.onInit = function () {
    const ctx = self.ctx;

    ctx.icon = ctx.settings.icon || 'mdi:alert-circle';
    ctx.color = ctx.settings.color || '#2196f3';
    ctx.unit = ctx.settings.unit || ''; 
    
    console.log(ctx.icon);
};

self.onDataUpdated = function () {
    const ctx = self.ctx;
    const data = ctx.data;

    if (!data?.length || !data[0].data?.length) return;

    const last = data[0].data[data[0].data.length - 1];
    const value = last[1];

    // Units if available
    //ctx.unit = data[0].dataKey.units || "";

    ctx.$container.find(".mini-value").text(`${value} ${ctx.unit}`);
};

self.onResize = function () {
    const ctx = self.ctx;
    const container = ctx.$container.find(".mini-card")[0];
    if (!container) return;

    const h = container.offsetHeight;

    const iconSize = h * 0.22;
    const valueSize = h * 0.25;

    //container.style.setProperty("--icon-size", `${iconSize}px`);
    container.style.setProperty("--value-size", `${valueSize}px`);
    
    
    const container_mat_icon = ctx.$container.find(".mat-icon")[0];
    if (!container_mat_icon) return;

    container_mat_icon.style.setProperty("--icon-size", `${iconSize}px`);
    //container.style.setProperty("--value-size", `${valueSize}px`);
};

