descriptor.templateCss:
.gauge-container {
  --gauge-color: #0056b8;
  --gauge-bg-color: rgba(150,150,150,0.15);
  --gauge-font-size: var(--gauge-font-size);
  --gauge-font-weight: 700;

  width: 100%;
  height: 100%;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  background: transparent;
  color: inherit;
}

.gauge-svg {
    margin-top: 3px;
  width: 100%;
  height: 100%;
}


.gauge-container {
  --gauge-thickness: 20px; /* default fallback */
}

/* Fondo del gauge */
.gauge-bg {
  fill: none;
  stroke: var(--gauge-bg-color);
  stroke-width: var(--gauge-thickness);
  stroke-linecap: round;
}

/* Barra de progreso */
.gauge-progress {
  fill: none;
  stroke: var(--gauge-color);
  stroke-width: var(--gauge-thickness);
  stroke-linecap: round;
  transition: stroke 0.3s ease;
}

/* Valor central */
.gauge-value {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: var(--gauge-font-size);
  font-weight: var(--gauge-font-weight);
  color: #212121;
  text-align: center;
  pointer-events: none;
}

descriptor.controllerScript:
self.onInit = function () {
  const ctx = self.ctx;

  ctx.parameter = ctx.settings.parameter || 'progress';
  ctx.minValue = ctx.settings.minValue || 0;
  ctx.maxValue = ctx.settings.maxValue || 100;
  ctx.unit = ctx.settings.unit || '%';

  // Container base
  ctx.$container.html(`
    <div class="gauge-container">
      <svg id="gaugeChart" class="gauge-svg"></svg>
      <div id="gaugeValue" class="gauge-value"></div>
    </div>
  `);
};

self.onDataUpdated = function () {
  const ctx = self.ctx;
  const data = ctx.data;
  if (!data?.length || !data[0].data?.length) return;

  var raw1 = data[0].data[0][1];
  var raw2 = data[1].data[0][1];
  var raw3 = data[2].data[0][1];
  var raw4 = data[3].data[0][1];
  var raw5 = data[4].data[0][1];
  var raw6 = data[5].data[0][1];

  var val = (raw1 / raw2) * (raw3 / raw4) * (raw5 * raw6);
    
  if (isNaN(val)) {
      val = 0;
  }

  const container = ctx.$container.find(".gauge-container")[0];
  const width = container.offsetWidth || 200;
  const height = container.offsetHeight || 200;
  const cx = width / 2;
  const cy = height / 2;

  const radius = Math.min(width, height) / 2.4;
  const thickness = radius * 0.25;
  container.style.setProperty('--gauge-thickness', `${thickness}px`);
  
  // Example: label font-size = 35% of radius
  const labelSize = radius * 0.4;
  // Update the CSS variable dynamically
  container.style.setProperty('--gauge-font-size', `${labelSize}px`);


  const color = getComputedStyle(container).getPropertyValue('--gauge-color').trim() || '#0056b8';
  const bgColor = getComputedStyle(container).getPropertyValue('--gauge-bg-color').trim() || 'rgba(150,150,150,0.15)';
  
  container.style.setProperty("--gauge-color", ctx.settings.color);

  const svg = d3.select(container).select("#gaugeChart");
  svg.selectAll("*").remove();
  svg.attr("width", width).attr("height", height);

  const scale = d3.scaleLinear().domain([ctx.minValue, ctx.maxValue]).range([0, 2 * Math.PI]);
  const angle = scale(val);
  const startAngle = -Math.PI / 2;
  const endAngle = startAngle + angle;

  function polar(cx, cy, r, angle) {
    return { x: cx + r * Math.cos(angle), y: cy + r * Math.sin(angle) };
  }

  // Background circle
  svg.append("circle")
    .attr("class", "gauge-bg")
    .attr("cx", cx)
    .attr("cy", cy)
    .attr("r", radius);

  // Foreground arc (progress)
  if (angle > 1e-6) {
    if (angle >= 2 * Math.PI - 1e-6) {
      svg.append("circle")
        .attr("class", "gauge-progress")
        .attr("cx", cx)
        .attr("cy", cy)
        .attr("r", radius);
    } else {
      const start = polar(cx, cy, radius, startAngle);
      const end = polar(cx, cy, radius, endAngle);
      const largeArc = angle > Math.PI ? 1 : 0;

      const d = `M ${start.x} ${start.y} A ${radius} ${radius} 0 ${largeArc} 1 ${end.x} ${end.y}`;
      svg.append("path")
        .attr("class", "gauge-progress")
        .attr("d", d);
    }
  }

  // Update central value
  ctx.$container.find("#gaugeValue").text(`${val.toFixed(0)}${ctx.unit}`);
};

self.onResize = function () {
  self.onDataUpdated();
};
