descriptor.templateCss:
.tb-scroll-container {
  position: relative;      /* stays within widget bounds */
  overflow-y: auto;        /* vertical scroll */
  overflow-x: hidden;
  -webkit-overflow-scrolling: touch; /* smooth on touch devices */
}

.mat-table {
  width: 100%;
  border-collapse: collapse;
}

.mat-cell {
  font-family: 'Roboto', sans-serif; /* Roboto font */
  font-size: 13px;                   /* Smaller font */
  padding: 13px 13px;                /* More vertical padding = extra space top/bottom */
  border-bottom: 1px solid #e0e0e0;
}

/*
.mat-row:first-child .mat-cell {
  border-top: 1px solid #e0e0e0;
}
*/

.key-cell {
  text-align: left;
  font-weight: normal;
  
  white-space: normal;       /* prevent wrapping */
  padding-right: 12px;        /* small gap before bar */
  max-width: 250px;          /* adjust as needed */
  min-width: 200px;          /* won‚Äôt shrink too much */
  width: 1%;                 /* force auto-shrink */
  overflow: hidden;
  /*text-overflow: ellipsis;    ‚Ä¶ if text too long */
}

.value-cell {
  text-align: left;          /* align bar to left edge */
  width: 100%;               /* take all remaining space */
}


.mat-row:hover { 
  background-color: #f5f5f5; 
}

.tb-search-container {
  padding: 6px 5px 25px 0px;
  background: transparent;
  border-bottom: 1px solid #e0e0e0;
  display: flex;               /* enable flexbox */
  justify-content: flex-end;   /* push search to the right */
}

.tb-search {
  width: 30%;                  /* half the container width */
  max-width: 250px;            /* optional: prevent it from growing too big */
  padding: 6px 10px;
  font-size: 13px;
  font-family: 'Roboto', sans-serif;
  border: 1px solid #ccc;
  border-radius: 4px;
}

.bar-container {
  display: flex;
  align-items: center;
  height: 16px;
}

.bar-fill {
  height: 100%;
  border-radius: 3px;
  transition: width 0.3s ease;   /* smooth update */
}

.bar-label {
  margin-left: 6px;
  font-size: 12px;
  color: #444;   /* darker for readability */
  white-space: nowrap;
}

descriptor.controllerScript:
self.onInit = function() {

    // üé® Colores del gradiente
    self.ctx.colorA = self.ctx.settings.color_a || "#4CAF50";
    self.ctx.colorB = self.ctx.settings.color_b || "#A5D7A7";

    // üß∞ Inicializar estructuras
    self.ctx.tables = [];
    self.ctx.maxValues = [];

    // üî≤ Limpiar cualquier contenido previo
    self.ctx.$container.empty();

    // üß© Crear contenedor principal √∫nico
    var containerId = 'tbDatasourceMain';
    self.ctx.$container.append(
        "<div id='" + containerId + "' class='tbDatasource-container'></div>"
    );
    var datasourceContainer = $('#' + containerId, self.ctx.$container);

    // üîç Crear buscador global (√∫nico)
    var searchId = 'searchGlobal';
    datasourceContainer.append(
        "<div class='tb-search-container'>" +
        "<input id='" + searchId + "' type='text' class='tb-search' placeholder='Buscar...'>" +
        "</div>"
    );

    // üßæ Crear tabla √∫nica (para todos los datasources)
    var tableId = 'tableGlobal';
    datasourceContainer.append(
        "<div class='tb-scroll-container'>" +
        "<table id='" + tableId + "' class='mat-table full-width'><tbody></tbody></table>" +
        "</div>"
    );

    // üíæ Guardar referencias
    var tableObj = {
        body: $('#' + tableId + ' tbody', self.ctx.$container),
        rows: {},
        searchInput: $('#' + searchId, self.ctx.$container)
    };
    self.ctx.tables.push(tableObj);

    // üîç Activar filtro global
    tableObj.searchInput.on('input', function() {
        var term = $(this).val().toLowerCase();
        $('tr', tableObj.body).each(function() {
            var text = $(this).text().toLowerCase();
            $(this).toggle(text.indexOf(term) > -1);
        });
    });

    // ü™ü Ajustar tama√±o
    self.onResize();
};



function hasMeaningfulValue(v) {
    if (v === null || v === undefined) return false;
    if (typeof v === 'number') return isFinite(v); // keep 0
    if (typeof v === 'boolean')
        return true; // keep false/true
    if (typeof v === 'string') {
        var s = v.trim();
        if (!s) return false; // "", "   "
        if (s === '-' || s === '‚Äî')
            return false; // common placeholders
        if (s.toLowerCase() === 'null' || s
            .toLowerCase() === 'undefined') return false;
        return true;
    }
    // objects/arrays: keep non-empty ones
    try {
        var s = JSON.stringify(v);
        return s && s !== '{}' && s !== '[]';
    } catch (e) {
        return false;
    }
}


self.onResize = function() {
    var widgetHeight = self.ctx.height || self.ctx.$container.height();
    $('.tb-scroll-container', self.ctx.$container).css({
        'height': 'auto',
        'max-height': widgetHeight + 'px',
        'overflow-y': 'auto'
    });
};



// Store per-datasource max values


// üü¢ When new data arrives
self.onDataUpdated = function() {
    // First pass: find max per datasource
    self.ctx.maxValues = [];
    for (var i = 0; i < self.ctx.data.length; i++) {
        var cellData = self.ctx.data[i];
        if (!cellData || !cellData.data || !cellData
            .data.length) continue;

        var raw = cellData.data[cellData.data.length -
            1][1];
        var dsIndex = (cellData.datasourceIndex !=
            null) ? cellData.datasourceIndex : 0;

        if (typeof raw === 'number' && isFinite(raw)) {
            if (self.ctx.maxValues[dsIndex] == null ||
                raw > self.ctx.maxValues[dsIndex]) {
                self.ctx.maxValues[dsIndex] = raw;
            }
        }
    }

    // Second pass: render rows
    for (var i = 0; i < self.ctx.data.length; i++) {
        var cellData = self.ctx.data[i];
        if (!cellData || !cellData.data || !cellData
            .data.length) continue;

        var tvPair = cellData.data[cellData.data
            .length - 1];
        var raw = tvPair[1];
        var dsIndex = (cellData.datasourceIndex !=
            null) ? cellData.datasourceIndex : 0;
        var key = cellData.dataKey.label || cellData
            .dataKey.name;
        var table = self.ctx.tables[dsIndex];
        if (!table) continue;

        if (!hasMeaningfulValue(raw)) {
            if (table.rows[key]) {
                table.rows[key].closest('tr').remove();
                delete table.rows[key];
            }
            continue;
        }

        var txt;
        if (typeof raw === 'number' && isFinite(raw)) {
            if (raw === 0) {//  Si el valor es 0, eliminar fila 
                if (table.rows[key]) {
                    table.rows[key].closest('tr').remove();
                    delete table.rows[key];
                }
                continue;
            }
            var maxVal = self.ctx.maxValues[dsIndex] ||
                1; // avoid div by 0
            var percent = Math.max(0, Math.min(100, (
                raw / maxVal) * 100));

            // build bar
            txt = '<div class="bar-container">' +
    '<div class="bar-fill" style="width:' + percent + '%; ' +
    'background: linear-gradient(90deg, ' + self.ctx.colorA + ', ' + self.ctx.colorB + ');"></div>' +
    '<span class="bar-label">' + raw + '</span>' +
    '</div>';

        } else if (typeof raw === 'object') {
            try {
                txt = JSON.stringify(raw);
            } catch (e) {
                txt = String(raw);
            }
        } else {
            txt = String(raw);
        }

        if (!table.rows[key]) {
            var safeId = 'row_' + dsIndex + '_' + key
                .replace(/[^\w-]/g, '_');
            var $row = $(
                "<tr class='mat-row' id='" +
                safeId + "'>" +
                "<td class='mat-cell key-cell'>" +
                key + "</td>" +
                "<td class='mat-cell value-cell'></td>" +
                "</tr>"
            );
            table.body.append($row);
            table.rows[key] = $row.find('.value-cell');
        }

        table.rows[key].html(txt);
    }
    
    // orden de top, mayor a menor
    for (var t = 0; t < self.ctx.tables.length; t++) {
    var tableObj = self.ctx.tables[t];
    var $rows = tableObj.body.find('tr').get();

    $rows.sort(function(a, b) {
        var aVal = parseFloat($(a).find('.bar-label').text()) || 0;
        var bVal = parseFloat($(b).find('.bar-label').text()) || 0;
        return bVal - aVal; 
    });

    // Reinsertar en el nuevo orden
    $.each($rows, function(idx, row) {
        tableObj.body.append(row);
    });
}
};

