descriptor.templateCss:
/* Two-column wrapper */
.gauge-wrapper {
  display: flex;
  gap: 15px;
  width: 100%;
  height: 100%;
  align-items: center;
}

.legend-box {
  display: flex;
  flex-direction: column;
  gap: 10px;
  font-size: 14px;
  padding-left: 10px;
}

.legend-item {
  display: flex;
  align-items: center;
  gap: 6px;
}

.legend-icon {
  width: 12px;
  height: 12px;
  flex-shrink: 0;
}

.legend-value {
  font-weight: 700;
  margin-left: 4px;
}


/*////////////////////////////////////////////*/


.gauge-container {
  --gauge-color: #0056b8;
  --gauge-bg-color: rgba(150,150,150,0.15);
  --gauge-font-size: var(--gauge-font-size);
  --gauge-font-weight: 700;

  width: 100%;
  height: 100%;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  background: transparent;
  color: inherit;
}

.gauge-svg {
    margin-top: 3px;
  width: 100%;
  height: 100%;
}


.gauge-container {
  --gauge-thickness: 20px; /* default fallback */
}

/* Fondo del gauge */
.gauge-bg {
  fill: none;
  stroke: var(--gauge-bg-color);
  stroke-width: var(--gauge-thickness);
  stroke-linecap: round;
}

/* Barra de progreso */
.gauge-progress {
  fill: none;
  stroke: var(--gauge-color);
  stroke-width: var(--gauge-thickness);
  stroke-linecap: round;
  transition: stroke 0.3s ease;
}

/* Valor central */
.gauge-value {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: var(--gauge-font-size);
  font-weight: var(--gauge-font-weight);
  color: #212121;
  text-align: center;
  pointer-events: none;
}

descriptor.controllerScript:
self.onInit = function () {
  //console.log(self.ctx.data);
    
  const ctx = self.ctx;

  ctx.parameter = ctx.settings.parameter || 'progress';
  ctx.minValue = ctx.settings.minValue || 0;
  ctx.maxValue = ctx.settings.maxValue || 100;
  ctx.unit = ctx.settings.unit || '%';
  ctx.color = ctx.settings.color || '#ff0000';
  
  ctx.$container.find("#legend-active-dot").attr("fill", ctx.settings.color);


    /*
  // Container base
  ctx.$container.html(`
    <div class="gauge-container">
      <svg id="gaugeChart" class="gauge-svg"></svg>
      <div id="gaugeValue" class="gauge-value"></div>
    </div>
  `);
  */
};

self.onDataUpdated = function () {
  const ctx = self.ctx;
  const data = ctx.data;
  if (!data?.length || !data[0].data?.length) return;
  
  let operarios_activos = 0;

  // Iterate through all data entries
    self.ctx.data.forEach(entry => {
        // Check if this entry corresponds to the desired dataKey
        if (entry.dataKey?.name === "nombre_operario" && entry.data?.length) {
            
            let rawValue = entry.data[0][1]; 
            if (rawValue) {
                try {
                    // Parse the JSON string into an array
                    const operatorArray = JSON.parse(rawValue);
    
                    // Ensure it's an array before counting
                    if (Array.isArray(operatorArray)) {
                        operarios_activos += operatorArray.length;
                    }
                } catch (e) {
                    console.warn("Failed to parse operator list:", rawValue, e);
                }
            }
        }
    });
    
    console.log("Total operators:", operarios_activos);


  var operarios_registrados = data[0].data[0][1];
  var ratio_meta = data[1].data[0][1];
  
  var meta_produccion = operarios_registrados * ratio_meta;

  const val = 100 * operarios_activos/operarios_registrados;
  console.log("activos: ", operarios_activos, "registrados: ", operarios_registrados);

  const container = ctx.$container.find(".gauge-container")[0];
  const width = container.offsetWidth || 200;
  const height = container.offsetHeight || 200;
  const cx = width / 2;
  const cy = height / 2;

  const radius = Math.min(width, height) / 2.4;
  const thickness = radius * 0.20;
  container.style.setProperty('--gauge-thickness', `${thickness}px`);
  
  // Example: label font-size = 35% of radius
  const labelSize = radius * 0.4;
  // Update the CSS variable dynamically
  container.style.setProperty('--gauge-font-size', `${labelSize}px`);


  const color = getComputedStyle(container).getPropertyValue('--gauge-color').trim() || '#0056b8';
  const bgColor = getComputedStyle(container).getPropertyValue('--gauge-bg-color').trim() || 'rgba(150,150,150,0.15)';
  
  container.style.setProperty("--gauge-color", ctx.settings.color);

  const svg = d3.select(container).select("#gaugeChart");
  svg.selectAll("*").remove();
  svg.attr("width", width).attr("height", height);

  const scale = d3.scaleLinear().domain([ctx.minValue, ctx.maxValue]).range([0, 2 * Math.PI]);
  const angle = scale(val);
  const startAngle = -Math.PI / 2;
  const endAngle = startAngle + angle;

  function polar(cx, cy, r, angle) {
    return { x: cx + r * Math.cos(angle), y: cy + r * Math.sin(angle) };
  }

  // Background circle
  svg.append("circle")
    .attr("class", "gauge-bg")
    .attr("cx", cx)
    .attr("cy", cy)
    .attr("r", radius);

  // Foreground arc (progress)
  if (angle > 1e-6) {
    if (angle >= 2 * Math.PI - 1e-6) {
      svg.append("circle")
        .attr("class", "gauge-progress")
        .attr("cx", cx)
        .attr("cy", cy)
        .attr("r", radius);
    } else {
      const start = polar(cx, cy, radius, startAngle);
      const end = polar(cx, cy, radius, endAngle);
      const largeArc = angle > Math.PI ? 1 : 0;

      const d = `M ${start.x} ${start.y} A ${radius} ${radius} 0 ${largeArc} 1 ${end.x} ${end.y}`;
      svg.append("path")
        .attr("class", "gauge-progress")
        .attr("d", d);
    }
  }

  // Update central value
  ctx.$container.find("#gaugeValue").text(`${operarios_activos.toFixed(0)}`);
  // Update legend values
ctx.$container.find("#legend-activos").text(operarios_activos);
ctx.$container.find("#legend-registrados").text(operarios_registrados);
ctx.$container.find("#legend-meta").text(meta_produccion.toFixed(1));

};

self.onResize = function () {
  self.onDataUpdated();
};
